var link=document.querySelector(".btn-modal");var mask=document.querySelector(".modal-mask");var popup=document.querySelector(".modal-feedback");var close=popup.querySelector(".modal-close");var name=popup.querySelector("[name=feedback-name]");var form=popup.querySelector(".modal-form");var email=popup.querySelector("[name=feedback-email]");var text=popup.querySelector("[name=feedback-text]");link.addEventListener("click",function(a){a.preventDefault();mask.classList.add("modal-show");popup.classList.add("modal-animation")});close.addEventListener("click",function(a){a.preventDefault();mask.classList.remove("modal-show");popup.classList.remove("modal-error")});form.addEventListener("submit",function(a){if(!name.value||!email.value||!text.value){a.preventDefault();popup.classList.remove("modal-error");popup.offsetWidth=popup.offsetWidth;popup.classList.add("modal-error")}});window.addEventListener("keydown",function(a){if(a.keyCode===27){a.preventDefault();if(mask.classList.contains("modal-show")){mask.classList.remove("modal-show");popup.classList.remove("modal-error")}}});